  def test_adminUsernameLogin(self):
    self.driver.get("http://127.0.0.1:5500/teton/1.6/admin.html")
    self.driver.set_window_size(1295, 767)
    self.driver.find_element(By.ID, "username").send_keys("wronguser")
    self.driver.find_element(By.ID, "password").click()
    self.driver.find_element(By.ID, "password").send_keys("wrongpassword")
    self.driver.find_element(By.CSS_SELECTOR, ".mysubmit:nth-child(4)").click()
    # wait for potential rendering/timing before asserting error text
    WebDriverWait(self.driver, 5).until(
      lambda d: "Invalid username and password." in d.page_source
    )
    assert self.driver.find_element(By.CSS_SELECTOR, ".errorMessage").text == "Invalid username and password."
    elements = self.driver.find_elements(By.ID, "username")
    assert len(elements) > 0
  
