  def test_joinFirstNameInputNextStepEmail(self):
    self.driver.get("http://127.0.0.1:5500/teton/1.6/join.html")
    self.driver.set_window_size(1295, 767)
    elements = self.driver.find_elements(By.NAME, "fname")
    assert len(elements) > 0
    self.driver.find_element(By.NAME, "fname").send_keys("gdgdjd")
    self.driver.find_element(By.NAME, "lname").click()
    self.driver.find_element(By.NAME, "lname").send_keys("gdhdjdk")
    self.driver.find_element(By.CSS_SELECTOR, ".myinput:nth-child(4)").click()
    self.driver.find_element(By.NAME, "bizname").send_keys("gdgdgd")
    self.driver.find_element(By.NAME, "biztitle").click()
    self.driver.find_element(By.NAME, "biztitle").send_keys("gsgsj")
    self.driver.find_element(By.NAME, "submit").click()
    # wait for email input to appear after submit/next step
    WebDriverWait(self.driver, 5).until(lambda d: d.find_elements(By.NAME, "email"))
    elements = self.driver.find_elements(By.NAME, "email")
    assert len(elements) > 0
